import MovieCart from "./movie/MovieCart";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import useSWR from "swr";
import { fetcher } from "../config";

// https://api.themoviedb.org/3/movie/now_playing?api_key=e8161bb2864d424df9c3ea695c54495e

const MoviePlaying = () => {
  return (
    <div className="page-container">
      <div className="mt-5 mb-5 p-2">
        <h2 className="title text-white font-bold text-4xl">Now Playing</h2>
      </div>
      <div className="movieList flex">
        <Swiper grabCursor={"true"} spaceBetween={20} slidesPerView={"auto"}>
          <SwiperSlide>
            <MovieCart></MovieCart>
          </SwiperSlide>
        </Swiper>
      </div>
    </div>
  );
};

export default MoviePlaying;
