import React, { useEffect, useState } from "react";
import useSWR from "swr";
import { fetcher } from "../../config";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCirclePlay, faStar } from "@fortawesome/free-regular-svg-icons";
import { useNavigate } from "react-router-dom";
import { useDebounce } from "use-debounce";
import ReactPaginate from "react-paginate";

const MovieList = () => {
  const [page, setPage] = useState(1);
  const [moviePage, setMoviePage] = useState();
  const [movieTotalPage, setMovieTotalPage] = useState();
  const [url, setUrl] = useState(
    "https://api.themoviedb.org/3/movie/popular?api_key=e8161bb2864d424df9c3ea695c54495e"
  );
  const [movieList, setMovieList] = useState([]);
  const [valueSearch, setValueSearch] = useState("");
  const [value] = useDebounce(valueSearch, 500);
  const navigate = useNavigate();
  const { data, error } = useSWR(url, fetcher);
  const loading = !data && !error;
  useEffect(() => {
    if (data && data.results) {
      setMovieList(data.results);
      setMoviePage(data.page);
      setMovieTotalPage(data.total_pages);
    }
  }, [data]);

  const handleSearch = (e) => {
    setValueSearch(e.target.value);
  };
  useEffect(() => {
    if (value) {
      setUrl(
        `https://api.themoviedb.org/3/search/movie?api_key=e8161bb2864d424df9c3ea695c54495e&query=${value}&page=${page}`
      );
    } else {
      setUrl(
        `https://api.themoviedb.org/3/movie/popular?api_key=e8161bb2864d424df9c3ea695c54495e&page=${page}`
      );
    }
  }, [value, page]);

  // phan trang react js
  const [itemOffset, setItemOffset] = useState(0);
  const [pageCount, setPageCount] = useState(0);
  const itemsPerPage = 20;
  useEffect(() => {
    setPageCount(Math.ceil(movieTotalPage / itemsPerPage));
  }, [itemOffset, movieTotalPage]);
  const handlePageClick = (event) => {
    const newOffset = (event.selected * itemsPerPage) % movieTotalPage;
    setItemOffset(newOffset);
    console.log(event.selected);
    setPage(event.selected + 1);
  };
  console.log("page", page);
  console.log(data);
  return (
    <>
      <div className="mt-5 page-container w-[500px] mx-auto">
        <div className="flex items-center gap-x-3">
          <input
            onChange={handleSearch}
            className="w-full outline-none px-3 py-2 h-[40px] rounded-lg bg-gray-300"
            placeholder="Enter your search movie"
            type="text"
          />
          <button className="bg-primary h-[40px] px-4 rounded-lg text-white">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              strokeWidth="1.5"
              stroke="currentColor"
              class="w-6 h-6"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d="m21 21-5.197-5.197m0 0A7.5 7.5 0 1 0 5.196 5.196a7.5 7.5 0 0 0 10.607 10.607Z"
              />
            </svg>
          </button>
        </div>
      </div>

      {loading && (
        <div className="w-10 h-10 rounded-full border-4 border-primary border-t-transparent border-t-4 mx-auto animate-spin absolute top-1/2 left-1/2 "></div>
      )}

      <div className="flex flex-wrap justify-center gap-5 mt-7">
        {!loading &&
          movieList.length > 0 &&
          movieList.map((item) => (
            <div
              key={item.id}
              className="movieItem text-white w-[300px] p-3 rounded-lg border flex flex-col border-gray-600 bg-slate-800"
            >
              <img
                className="w-full h-[250px] object-cover rounded-lg "
                src={`http://image.tmdb.org/t/p/w500/${item.poster_path}`}
                alt=""
              />
              <div className="flex flex-col flex-1">
                <h3 className="mt-3 mb-2 text-lg">{item.title}</h3>
                <div className="flex items-center justify-between mt-auto mb-4">
                  <span className="text-gray-400">
                    {new Date(item.release_date).getFullYear()}
                  </span>
                  <span>
                    {item.vote_average}
                    <FontAwesomeIcon
                      icon={faStar}
                      className="text-yellow-400 ml-2"
                    />
                  </span>
                </div>
                <button
                  onClick={() => navigate(`/movie/${item.id}`)}
                  className="watch bg-primary w-full mt-auto h-[50px] px-8 rounded-lg flex items-center justify-center cursor-pointer text-xl font-semibold gap-2"
                >
                  <h2>Watch Now</h2>
                  <FontAwesomeIcon icon={faCirclePlay} className="text-3xl" />
                </button>
              </div>
            </div>
          ))}
      </div>

      <div className="page-container flex items-center justify-center text-white mt-5">
        <ReactPaginate
          breakLabel="..."
          nextLabel="next >"
          onPageChange={handlePageClick}
          pageRangeDisplayed={5}
          pageCount={pageCount}
          previousLabel="< previous"
          renderOnZeroPageCount={null}
        />
      </div>

      {/* <div className="page-container flex items-center justify-center text-white mt-5">
        <svg
          xmlns="http://www.w3.org/2000/svg"
          fill="none"
          viewBox="0 0 24 24"
          strokeWidth={3}
          stroke="currentColor"
          className="w-6 h-6 cursor-pointer"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            d="M15.75 19.5 8.25 12l7.5-7.5"
          />
        </svg>
        <span className="bg-gray-400 px-3 py-1 rounded-lg ml-2 mr-2">1</span>
        <svg
          xmlns="http://www.w3.org/2000/svg"
          fill="none"
          viewBox="0 0 24 24"
          strokeWidth="3"
          stroke="currentColor"
          class="w-6 h-6 cursor-pointer"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            d="m8.25 4.5 7.5 7.5-7.5 7.5"
          />
        </svg>
      </div> */}
    </>
  );
};

export default MovieList;
