import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import useSWR from "swr";
import { fetcher } from "../../config";
import Header from "../Header";
const MovieDetailsPage = () => {
  const { movieID } = useParams();
  const [movieDetail, setMovieDetail] = useState([]);
  const { data } = useSWR(
    `https://api.themoviedb.org/3/movie/${movieID}?api_key=e8161bb2864d424df9c3ea695c54495e`,
    fetcher
  );
  console.log(data);
  return (
    <div>
      <Header></Header>
      {data && (
        <div className="relative">
          <div className="w-full relative">
            <div className="overlay absolute inset-0 bg-black opacity-70"></div>
            <img
              className="w-full h-[700px]"
              src={`http://image.tmdb.org/t/p/original/${data.backdrop_path}`}
              alt=""
            />
          </div>
          <img
            className="w-[900px] h-[400px] absolute bottom-[-25%] left-[50%] translate-x-[-50%] rounded-xl"
            src={`http://image.tmdb.org/t/p/original/${data.backdrop_path}`}
            alt=""
          />
        </div>
      )}
    </div>
  );
};

export default MovieDetailsPage;
