  );
  useEffectect(() => {
    if (data && data.results) {
      setMovie(data.results);
    }
  }, [data]);
  console.log(movie);

  return (
    <div className="page-container relative h-[500px] rounded-lg mt-8">
      <div className="overlay absolute inset-0 bg-black opacity-20"></div>
      <img
        className="w-full h-full object-cover rounded-lg"
        src="https://getwallpapers.com/wallpaper/full/4/c/1/1110427-full-size-the-avenger-wallpaper-hd-1920x1080-desktop.jpg"
        alt=""
      />
      <div className="absolute top-[50%] text-white left-10">
        <h2 className="text-5xl font-bold ">Avengers: Endgame</h2>
        <div className="movie__type flex items-center gap-3 mt-2">
          <span className="bg-transparent border px-4 py-1 rounded-lg">
            Action
          </span>
          <span className="bg-transparent border px-4 py-1 rounded-lg">
            Avenger
          </span>
          <span className="bg-transparent border px-4 py-1 rounded-lg">
            Drama
          </span>
        </div>
        <div className="flex items-center gap-5 mt-5">
          <div className="watch bg-primary w-[220px] h-[50px] px-10 rounded-lg flex items-center justify-center cursor-pointer text-xl font-semibold gap-2">
            <h2>Watch</h2>
            <FontAwesomeIcon icon={faCirclePlay} className="text-3xl" />
          </div>
          <div className="bg-gray-500 flex items-center px-4 text-4xl cursor-pointer h-[50px] rounded-lg">
            +
          </div>
        </div>
      </div>
    </div>
  );
};

export default Banner;
