import { faCirclePlay, faStar } from "@fortawesome/free-regular-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import React, { useEffect, useState } from "react";
import useSWR from "swr";
import { fetcher } from "../../config";
import Swiper from "swiper";

const MovieCart = (props) => {
  const [movie, setMovie] = useState([]);
  const { data, error, isLoading } = useSWR(
    "https://api.themoviedb.org/3/movie/now_playing?api_key=e8161bb2864d424df9c3ea695c54495e",
    fetcher
  );
  useEffect(() => {
    if (data && data.results) {
      setMovie(data.results);
    }
  }, [data]);
  console.log(movie);
  return (
    <div>
      <Swiper grabCursor={"true"} spaceBetween={20} slidesPerView={"auto"}>
        <SwiperSlide></SwiperSlide>
      </Swiper>
      {movie.length > 0 &&
        movie.map((item) => (
          <div className="movieItem h-[100%] text-white w-[250px] p-3 rounded-lg border flex flex-col border-gray-600 bg-slate-800">
            <img
              className="w-full h-[250px] object-cover rounded-lg "
              src={`http://image.tmdb.org/t/p/w500/${item.poster_path}`}
              alt=""
            />
            <div className="flex flex-col flex-1">
              <h3 className="mt-auto mb-2 text-lg">{item.title}</h3>
              <div className="flex items-center justify-between mt-auto mb-4">
                <span className="text-gray-400">
                  {new Date(item.release_date).getFullYear()}
                </span>
                <span>
                  {item.vote_average}
                  <FontAwesomeIcon
                    icon={faStar}
                    className="text-yellow-400 ml-2"
                  />
                </span>
              </div>
              <div className="watch bg-primary w-full mt-auto h-[50px] px-8 rounded-lg flex items-center justify-center cursor-pointer text-xl font-semibold gap-2">
                <h2>Watch Now</h2>
                <FontAwesomeIcon icon={faCirclePlay} className="text-3xl" />
              </div>
            </div>
          </div>
        ))}
    </div>
  );
};

export default MovieCart;
