import { faCirclePlay, faStar } from "@fortawesome/free-regular-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import React from "react";

const MovieCart = (props) => {
  return (
    <div className="movieItem flex flex-col h-full text-white w-[250px] p-3 rounded-lg border border-gray-600 bg-slate-800">
      <img
        className="w-full h-[250px] object-cover rounded-lg"
        src={props.img}
        alt=""
      />
      <div className="flex flex-col flex-1">
        <h3 className="py-2 text-lg">{props.name}</h3>
        <div className="flex items-center justify-between">
          <span className="text-gray-400">{props.year}</span>
          <span>
            {props.vote}
            <FontAwesomeIcon icon={faStar} className="text-yellow-400 ml-2" />
          </span>
        </div>
        <div className="watch bg-primary w-full mt-4 h-[50px] px-8 rounded-lg flex items-center justify-center cursor-pointer text-xl font-semibold gap-2">
          <h2>Watch Now</h2>
          <FontAwesomeIcon icon={faCirclePlay} className="text-3xl" />
        </div>
      </div>
    </div>
  );
};

export default MovieCart;
