import React from "react";
import useSWR from "swr";
import "swiper/css";
import Header from "../Header";
import { fetcher } from "../../config";
import { useParams } from "react-router-dom";
import { Swiper, SwiperSlide } from "swiper/react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCirclePlay, faStar } from "@fortawesome/free-regular-svg-icons";
const MovieDetailsPage = () => {
  const { movieID } = useParams();
  const { data } = useSWR(
    `https://api.themoviedb.org/3/movie/${movieID}?api_key=e8161bb2864d424df9c3ea695c54495e`,
    fetcher
  );
  return (
    <div>
      <Header></Header>
      {data && (
        <>
          <div>
            <div className="w-full relative">
              <div className="overlay absolute inset-0 bg-black opacity-60"></div>
              <img
                className="w-full h-[700px]"
                src={`http://image.tmdb.org/t/p/original/${data.backdrop_path}`}
                alt=""
              />
            </div>
            <img
              className="w-[900px] h-[400px] mx-auto mt-[-200px] rounded-xl relative z-10"
              src={`http://image.tmdb.org/t/p/original/${data.backdrop_path}`}
              alt=""
            />
          </div>
          <h2 className="text-center text-white text-3xl font-bold mt-7">
            {data.title}
          </h2>
          <div className="movie__type flex items-center gap-3 mt-5 text-white justify-center">
            {data.genres.length > 0 &&
              data.genres.map((item) => (
                <div key={item.id}>
                  <span className="bg-transparent border px-4 py-1 rounded-lg">
                    {item.name}
                  </span>
                </div>
              ))}
          </div>
          <p className="text-white w-[700px] text-center mx-auto mt-5">
            {data.overview}
          </p>
        </>
      )}
      <MovieCredits></MovieCredits>
      <MovieTrailer></MovieTrailer>
      <MovieSimilar></MovieSimilar>
    </div>
  );
};

const MovieCredits = () => {
  const { movieID } = useParams();
  const { data } = useSWR(
    `https://api.themoviedb.org/3/movie/${movieID}/credits?api_key=e8161bb2864d424df9c3ea695c54495e`,
    fetcher
  );
  if (!data) return;
  const { cast } = data;
  return (
    <div className="movieDetailPage page-container mt-10">
      <h1 className="text-center text-4xl font-bold text-white my-4">Cast</h1>
      <Swiper grabCursor={"true"} spaceBetween={20} slidesPerView={"auto"}>
        {cast.length > 0 &&
          cast.map((item) => (
            <SwiperSlide key={item.id}>
              <div className="w-[300px]">
                {item.profile_path === null ? (
                  <img
                    className="w-full h-[400px] rounded-lg"
                    src="http://meta.com.sg/wp-content/uploads/2015/09/blank-avatar.png"
                    alt=""
                  />
                ) : (
                  <img
                    className="w-full h-[400px] rounded-lg"
                    src={`http://image.tmdb.org/t/p/w500/${item.profile_path}`}
                    alt=""
                  />
                )}

                <h2 className="text-white font-semibold text-center text-xl mt-4">
                  {item.name}
                </h2>
              </div>
            </SwiperSlide>
          ))}
      </Swiper>
    </div>
  );
};

const MovieTrailer = () => {
  const { movieID } = useParams();
  const { data } = useSWR(
    `https://api.themoviedb.org/3/movie/${movieID}/videos?api_key=e8161bb2864d424df9c3ea695c54495e`,
    fetcher
  );
  if (!data) return;
  return (
    <div className="page-container">
      {data.results.slice(0, 2).map((item) => (
        <div key={item.id}>
          <div>
            <h3 className="bg-second px-5 py-2 rounded-lg inline-block text-white mt-7 mb-3 text-xl">
              {item.name}
            </h3>
          </div>
          <div className="w-full aspect-auto">
            <iframe
              className="rounded-xl"
              width="1280"
              height="500"
              src={`https://www.youtube.com/embed/${item.key}`}
              title="The Shawshank Redemption | Trailer | Warner Bros. Entertainment"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
              allowFullScreen
            ></iframe>
          </div>
        </div>
      ))}
    </div>
  );
};

const MovieSimilar = () => {
  const { movieID } = useParams();
  const { movieSimilar } = useSWR(
    `https://api.themoviedb.org/3/movie/${movieID}/similar?api_key=e8161bb2864d424df9c3ea695c54495e`,
    fetcher
  );
  if (!movieSimilar) return;
  console.log(movieSimilar);
  return (
    <div>
      <div className="flex flex-wrap justify-center gap-5 mt-7">
        {movieSimilar.length > 0 &&
          movieSimilar.map((item) => (
            <div className="movieItem text-white w-[300px] p-3 rounded-lg border flex flex-col border-gray-600 bg-slate-800">
              <img
                className="w-full h-[250px] object-cover rounded-lg "
                src={`http://image.tmdb.org/t/p/w500/${item.poster_path}`}
                alt=""
              />
              <div className="flex flex-col flex-1">
                <h3 className="mt-3 mb-2 text-lg">{item.name}</h3>
                <div className="flex items-center justify-between mt-auto mb-4">
                  <span className="text-gray-400">
                    {new Date(item.first_air_date).getFullYear()}
                  </span>
                  <span>
                    {item.vote_average}
                    <FontAwesomeIcon
                      icon={faStar}
                      className="text-yellow-400 ml-2"
                    />
                  </span>
                </div>
                <div className="watch bg-primary w-full mt-auto h-[50px] px-8 rounded-lg flex items-center justify-center cursor-pointer text-xl font-semibold gap-2">
                  <h2>Watch Now</h2>
                  <FontAwesomeIcon icon={faCirclePlay} className="text-3xl" />
                </div>
              </div>
            </div>
          ))}
      </div>
    </div>
  );
};
export default MovieDetailsPage;
